hats = ["""
         --/--
         __|__
      .-'     `-.
     /           \\
    |-- __________|
    [__|__________| """, """
         --\--
         __|__
      .-'     `-.
     /           \\
    |-- __________|
    [__|__________| """, """
           |
         __|__
      .-'     `-.
     /           \\
    |-- __________|
    [__|__________| """]


def color_spinner():
    import time
    import os
    from termcolor import colored, COLORS
    import itertools
    color_maker = itertools.cycle(COLORS)
    while True:
        for hat in hats:
            os.system('cls')
            color = next(color_maker)
            print(colored(hat, color))
            time.sleep(.2)
